import React, { useState, useEffect } from "react";
import "../styles/tour.css";
import TourCard from "./../shared/TourCard";
import SearchBar from "./../shared/SearchBar";
import Newsletter from "./../shared/Newsletter";
import CommonSection from "../shared/CommonSection";
import { Container, Row, Col } from "reactstrap";
import axios from "axios"; // Import Axios
import { BASE_URL } from "../utils/config";

const Tours = () => {
  const [tours, setTours] = useState([]);
  const [tourCount, setTourCount] = useState(0);
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch tours data
  useEffect(() => {
    const fetchTours = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await axios.get(`${BASE_URL}/tours?page=${page}`);
        setTours(res.data); // Cập nhật state với dữ liệu tour
        setLoading(false);
      } catch (err) {
        setError(err);
        setLoading(false);
      }
    };

    fetchTours();
  }, [page]);

  // Fetch tour count
  useEffect(() => {
    const fetchTourCount = async () => {
      try {
        const res = await axios.get(`${BASE_URL}/tours/search/getTourCount`);
        setTourCount(res.data); // Cập nhật số lượng tours
      } catch (err) {
        setError(err);
      }
    };

    fetchTourCount();
  }, []);

  // Tính toán số lượng trang
  useEffect(() => {
    const pages = Math.ceil(tourCount / 8); // Giả sử mỗi trang hiển thị 8 tours
    setPageCount(pages);
    window.scrollTo(0, 0);
  }, [tourCount]);

  return (
    <>
      <CommonSection title={"All Tours"} />
      <section className="pt-0 mt-xl-5">
        <Container>
          {loading && <h3 className="text-center pt-5">Loading...</h3>}
          {error && <h3 className="text-center pt-5">Error: {error.message}</h3>}
          {!loading && !error && (
            <Row>
              {tours?.map((tour) => (
                <Col lg="3" md="6" sm="6" className="mb-4" key={tour._id}>
                  <TourCard tour={tour} />
                </Col>
              ))}

              <Col lg="12">
                <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                  {[...Array(pageCount).keys()].map((number) => (
                    <span
                      key={number}
                      onClick={() => setPage(number)}
                      className={page === number ? "active_page" : ""}
                    >
                      {number + 1}
                    </span>
                  ))}
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default Tours;
