import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "reactstrap";
import useFetch from "../../hooks/useFetch";
import { BASE_URL } from "./../../utils/config";
import TourCard from "../../shared/TourCard";
import FeaturedTourList from "./FeaturedTourList";
import CommonSection from "../../shared/CommonSection";

const TourListPage = () => {
  const [page, setPage] = useState(0);
  const { data: tours, loading: toursLoading, error: toursError } = useFetch(`${BASE_URL}/tours?page=${page}`);
  const { data: featuredTours, loading: featuredLoading, error: featuredError } = useFetch(`${BASE_URL}/tours/search/getFeaturedTours`);

  return (
    <>
      <CommonSection title={"All Tours"} />

      <section className="pt-0 mt-xl-5">
        <Container>
          {/* Hiển Thị Danh Sách Tour Nổi Bật */}
          {featuredLoading && <h3 className="text-center pt-5">Loading featured tours...</h3>}
          {featuredError && <h3 className="text-center pt-5">Error: {featuredError.message}</h3>}
          {!featuredLoading && !featuredError && (
            <Row>
              <Col lg="12" className="mb-5">
                <h2 className="feature_tour-title">Our Featured Tours</h2>
                {featuredTours?.map((tour) => (
                  <Col lg="3" className="mb-4 d-inline-flex" key={tour._id}>
                    <TourCard tour={tour} />
                  </Col>
                ))}
              </Col>
            </Row>
          )}

          {/* Hiển Thị Danh Sách Tour */}
          {toursLoading && <h3 className="text-center pt-5">Loading tours...</h3>}
          {toursError && <h3 className="text-center pt-5">Error: {toursError.message}</h3>}
          {!toursLoading && !toursError && (
            <Row>
              {tours?.map((tour) => (
                <Col lg="3" className="mb-4" key={tour._id}>
                  <TourCard tour={tour} />
                </Col>
              ))}

              <Col lg="12">
                <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                  {[...Array(Math.ceil(tours.length / 8)).keys()].map((number) => (
                    <span
                      key={number}
                      onClick={() => setPage(number)}
                      className={page === number ? "active_page" : ""}
                    >
                      {number + 1}
                    </span>
                  ))}
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
    </>
  );
};

export default TourListPage;
