const calculateAvgRating = (reviews) => {
  if (!reviews || reviews.length === 0) {
    return { totalRating: 0, avgRating: 'Not rated' }; // Hoặc '0.0' tùy thuộc vào yêu cầu
  }

  const totalRating = reviews.reduce((acc, item) => acc + (item.rating || 0), 0);
  const avgRating = totalRating / reviews.length;

  // Kiểm tra nếu avgRating là NaN hoặc không phải số
  const formattedAvgRating = isNaN(avgRating) ? 'Not rated' : avgRating.toFixed(1);

  return {
    totalRating,
    avgRating: formattedAvgRating
  };
};

export default calculateAvgRating;
