import React, { useEffect, useState } from 'react';
import { Table, Button } from 'reactstrap';
import { BASE_URL } from '../utils/config';

const Dashboard = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const token = localStorage.getItem('token'); // Lấy token từ localStorage
        const response = await fetch(`${BASE_URL}/tours`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          navigate("/login");
            return;
        }
        const data = await response.json();
        setTours(data.data); // Cập nhật theo cấu trúc dữ liệu của bạn
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchTours();
  }, []);

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token'); // Lấy token từ localStorage
      const response = await fetch(`${BASE_URL}/tours/${id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error('Error deleting tour');
      }
      setTours(tours.filter(tour => tour._id !== id)); // Cập nhật danh sách sau khi xóa
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Danh Sách Tour</h2>
      {loading && <h3 className="text-center">Loading...</h3>}
      {error && <h3 className="text-center text-danger">Error: {error}</h3>}
      {!loading && !error && (
        <Table striped>
          <thead>
            <tr>
              <th>#</th>
              <th>Tên Tour</th>
              <th>Mô Tả</th>
              <th>Giá</th>
              <th>Hành Động</th>
            </tr>
          </thead>
          <tbody>
            {tours.map((tour, index) => (
              <tr key={tour._id}>
                <td>{index + 1}</td>
                <td>{tour.title}</td>
                <td>{tour.desc}</td>
                <td>{tour.price}</td>
                <td>
                  <Button color="danger" onClick={() => handleDelete(tour._id)}>Xóa</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default Dashboard;
