import React, { useContext, useEffect, useRef, useState } from "react";
import "../styles/tour-detail.css"; // Thay đổi đường dẫn nếu cần
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import calculateAvgRating from "../utils/avgRating"; // Đường dẫn đúng đến hàm calculateAvgRating
import avatar from "../assets/images/avatar.jpg"; // Đường dẫn đúng đến ảnh avatar
import Booking from "../components/Booking/Booking"; // Đường dẫn đúng đến component Booking
import Newsletter from "./../shared/Newsletter"; // Đường dẫn đúng đến component Newsletter
import useFetch from "../hooks/useFetch"; // Đường dẫn đúng đến hook useFetch
import { BASE_URL } from "./../utils/config"; // Đường dẫn đúng đến file config
import { AuthContext } from "./../context/AuthContext"; // Đường dẫn đúng đến context AuthContext
import moment from 'moment-timezone';

// Component dự báo thời tiết
const WeatherForecast = ({ weatherData }) => {
  return (
    <Container className="container">
      <div className="hack1">
        <div className="hack2">
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Condition</th>
                <th>Temperature (°C)</th>
              </tr>
            </thead>
            <tbody>
              {weatherData.forecast.forecastday.map((day) => (
                <tr key={day.date}>
                  <td>{day.date}</td>
                  <td>{day.day.condition.text}</td>
                  <td>{day.day.avgtemp_c}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </Container>
  );
};

// CSS
const styles = `
body {
  background-color: #ccc;
}

.container {
  width: 300px;
  background-color: white;
}

table {
  width: 100%;
  border-collapse: collapse;
}

td, th {
  border: 1px solid black;
  padding: 8px;
  text-align: left;
}

/* try removing the "hack" below to see how the table overflows the .body */
.hack1 {
  display: table;
  table-layout: fixed;
  width: 100%;
}

.hack2 {
  display: table-cell;
  overflow-x: auto;
  width: 100%;
}
`;

// Component TourDetail
const TourDetail = () => {
  const { id } = useParams();
  const reviewMsgRef = useRef("");
  const [tourRating, setTourRating] = useState(null);
  const [weatherData, setWeatherData] = useState(null);
  const { user } = useContext(AuthContext);

  // Fetch data từ database
  const { data: tours, loading, error } = useFetch(`${BASE_URL}/tours/${id}`);

  // Destructure tour properties
  const {
    photo,
    title,
    desc,
    price,
    address,
    reviews,
    city,
    distance,
    maxGroupSize,
  } = tours || {}; // Thêm || {} để tránh lỗi khi tours là undefined

  const { totalRating, avgRating } = calculateAvgRating(reviews);

  // Convert date to Vietnam Time
  const convertToVietnamTime = (date) => {
    return moment(date).tz('Asia/Ho_Chi_Minh').format('MMMM D, YYYY HH:mm');
  };

  // Submit review handler
  const submitHandler = async (e) => {
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value;

    try {
      if (!user) {
        alert("Please log in to review a tour");
        return;
      }

      if (reviewText === "") {
        alert("Please enter a review");
        return;
      }

      if (tourRating === null) {
        alert("Please select a rating");
        return;
      }

      const reviewObj = {
        username: user.username,
        reviewText,
        rating: tourRating,
      };

      const res = await fetch(`${BASE_URL}/reviews/${id}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify(reviewObj),
      });

      const result = await res.json();
      if (!res.ok) {
        alert(result.message);
        return;
      }

      alert(result.message);
      
    } catch (error) {
      console.error(error);
    }
  };

  // Fetch weather data
  useEffect(() => {
    const fetchWeatherData = async () => {
      const apiKey = 'YOUR_ACTUAL_API_KEY'; // Thay thế bằng khóa API thực tế của bạn
      const response = await fetch(`http://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${city}&days=7`);
      const data = await response.json();
      setWeatherData(data);
    };

    if (city) {
      fetchWeatherData();
    }
  }, [city]);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [tours]);

  // Insert styles vào document
  useEffect(() => {
    const styleSheet = document.createElement("style");
    styleSheet.type = "text/css";
    styleSheet.innerText = styles;
    document.head.appendChild(styleSheet);
  }, []);

  return (
    <>
      <section>
        <Container>
          {loading && <h3 className="text-center pt-5">Loading...</h3>}
          {error && (
            <h3 className="text-center pt-5">Error: {error.message}</h3>
          )}
          {!loading && !error && (
            <Row>
              <Col lg="8">
                <div className="tour_content">
                  <img src={photo} alt="" />

                  <div className="tour_info">
                    <h2>{title}</h2>

                    <div className="d-flex align-items-center gap-5">
                      <span className="tour_rating d-flex align-items-center gap-1">
                        <i
                          className="ri-star-fill"
                          style={{ color: "var(--secondary-color)" }}
                        ></i>{" "}
                        {totalRating === 0 ? "Not rated" : avgRating}
                        {totalRating === 0 ? null : (
                          <span>({reviews?.length})</span>
                        )}
                      </span>

                      <span>
                        <i className="ri-map-pin-user-fill"></i> {address}
                      </span>
                    </div>

                    <div className="tour_extra-details">
                      <span>
                        <i className="ri-map-pin-2-line"></i> {city}
                      </span>
                      <span>
                        <i className="ri-money-dollar-circle-line"></i> ${price}{" "}
                        /per person
                      </span>
                      <span>
                        <i className="ri-map-pin-time-line"></i> {distance} km
                      </span>
                      <span>
                        <i className="ri-group-line"></i>
                        {maxGroupSize} people
                      </span>
                    </div>
                    <h5>Description</h5>
                    <p>{desc}</p>
                  </div>
                  <div>
                    {/* =================== Weather Info Start =============== */}
                    {weatherData && <WeatherForecast weatherData={weatherData} />}
                    {/* =================== Weather Info End =============== */}
                  </div>
                  {/* =================== Tour Reviews Start ===============  */}
                  <div className="tour_reviews mt-4">
                    <h4>Reviews ({reviews?.length} reviews)</h4>

                    <Form onSubmit={submitHandler}>
                      <div className="d-flex align-items-center gap-3 mb-4 rating_group">
                        {[1, 2, 3, 4, 5].map((rating) => (
                          <span
                            key={rating}
                            onClick={() => setTourRating(rating)}
                          >
                            <i
                              className={
                                tourRating >= rating
                                  ? "ri-star-s-fill"
                                  : "ri-star-s-line"
                              }
                            ></i>
                          </span>
                        ))}
                      </div>

                      <div className="review_input">
                        <input
                          type="text"
                          ref={reviewMsgRef}
                          placeholder="Share your thoughts"
                          required
                        />
                        <button
                          className="btn primary__btn text-white"
                          type="submit"
                        >
                          Submit
                        </button>
                      </div>
                    </Form>
                    <ListGroup className="user_reviews">
                      {reviews?.map((review, index) => (
                        <div className="review_item" key={index}>
                          <img src={avatar} alt="" />
                          <div className="w-100">
                            <div className="d-flex align-items-center justify-content-between">
                              <div>
                                <h5>{review.username}</h5>
                                <p>
                                  {convertToVietnamTime(review.createdAt)}
                                </p>
                              </div>
                              <span className="d-flex align-items-center">
                                {review.rating}{" "}
                                <i className="ri-star-s-fill"></i>
                              </span>
                            </div>
                            <p>{review.reviewText}</p>
                          </div>
                        </div>
                      ))}
                    </ListGroup>
                  </div>
                  {/* =================== Tour Reviews End =============== */}
                </div>
              </Col>
              <Col lg="4">
                <Booking tour={tours} />
              </Col>
            </Row>
          )}
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default TourDetail;
