import React, { useState, useEffect } from "react";
import "../styles/tour.css";
import TourCard from "./../shared/TourCard";
import Newsletter from "./../shared/Newsletter";
import CommonSection from "../shared/CommonSection";
import { Container, Row, Col } from "reactstrap";
import axios from "axios"; // Import Axios
import { BASE_URL } from "../utils/config";

const Tours = () => {
  const [tours, setTours] = useState([]);
  const [tourCount, setTourCount] = useState(0);
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        // Fetch tours data
        const toursRes = await axios.get(`${BASE_URL}/tours?page=${page}`);
        console.log(toursRes.data); // Kiểm tra dữ liệu nhận được
        setTours(toursRes.data);

        // Fetch tour count
        const countRes = await axios.get(`${BASE_URL}/tours/search/getTourCount`);
        setTourCount(countRes.data);

        const pages = Math.ceil(countRes.data / 8);
        setPageCount(pages);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [page]);

  return (
    <>
      <CommonSection title={"All Tours"} />
      <section className="pt-0 mt-xl-5">
        <Container>
          {loading && <h3 className="text-center pt-5">Loading...</h3>}
          {error && <h3 className="text-center pt-5">Error: {error.message}</h3>}
          {!loading && !error && (
            <Row>
              {Array.isArray(tours) && tours.length > 0 ? (
                tours.map((tour) => (
                  <Col lg="3" md="6" sm="6" className="mb-4" key={tour._id}>
                    <TourCard tour={tour} />
                  </Col>
                ))
              ) : (
                <Col lg="12">
                  <h3 className="text-center pt-5">No tours available.</h3>
                </Col>
              )}
              <Col lg="12">
                <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                  {[...Array(pageCount).keys()].map((number) => (
                    <span
                      key={number}
                      onClick={() => setPage(number)}
                      className={page === number ? "active_page" : ""}
                    >
                      {number + 1}
                    </span>
                  ))}
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default Tours;
