import React from "react";
import { Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "./tour-card.css";
import calculateAvgRating from "../utils/avgRating";
import axios from 'axios';  // Import Axios
import { BASE_URL } from "../utils/config"; // Đường dẫn API

const TourCard = ({ tour }) => {
  const { _id, title = "No Title", city = "Unknown City", photo = "", price = 0, featured = false, reviews = [] } = tour;
  const { totalRating, avgRating } = calculateAvgRating(reviews);

  // Hàm để lấy chi tiết tour
  const fetchTourDetails = async (id) => {
    try {
      const res = await axios.get(`${BASE_URL}/tours/${id}`);
      console.log(res.data); // Bạn có thể xử lý dữ liệu tour chi tiết ở đây
      // Có thể điều hướng hoặc làm gì đó với dữ liệu đã lấy được
    } catch (error) {
      console.error("Error fetching tour details:", error);
    }
  };

  return (
    <div className="tour_container">
      <div className="tour_card">
        <Card>
          <div className="tour_img">
            <img src={photo} alt={`Tour image for ${title}`} />
            {featured && <span>Featured</span>}
          </div>

          <CardBody>
            <div className="card_top d-flex align-items-center justify-content-between">
              <span className="tour_location d-flex align-items-center gap-1">
                <i className="ri-map-pin-line"></i> {city}
              </span>
              <span className="tour_rating d-flex align-items-center gap-1">
                <i className="ri-star-fill star"></i>{" "}
                {avgRating === 0 ? null : avgRating}
                {totalRating === 0 ? "Not rated" : <span>({reviews.length})</span>}
              </span>
            </div>

            <h5 className="tour_title">
              <Link to={`/tour/${_id}`} onClick={() => fetchTourDetails(_id)}>{title}</Link>
            </h5>
            <div className="card_bottom d-flex align-items-center justify-content-between mt-3">
              <h5>
                ${price} <span>/per person/</span>
              </h5>
              <Link to={`/tour/${_id}`} className="booking_link">
                <button className="booking_btn" onClick={() => fetchTourDetails(_id)}>Book Now</button>
              </Link>
            </div>
          </CardBody>
        </Card>
      </div>
    </div>
  );
};

export default TourCard;
