const calculateAvgRating = (reviews) => {
  // Kiểm tra nếu reviews là mảng hợp lệ và có ít nhất 1 phần tử
  if (!reviews || reviews.length === 0) {
    return { totalRating: 0, avgRating: "Not rated" };
  }

  // Tính tổng điểm rating
  const totalRating = reviews.reduce((acc, item) => acc + (item.rating || 0), 0);

  // Tính điểm trung bình
  const avgRating = totalRating === 0
    ? "Not rated" // Nếu không có rating nào
    : (totalRating / reviews.length).toFixed(1); // Tính trung bình và làm tròn 1 chữ số

  return {
    totalRating,
    avgRating
  };
};

export default calculateAvgRating;
