const calculateAvgRating = (reviews) => {
  // Kiểm tra nếu không có đánh giá
  if (!reviews || reviews.length === 0) {
    return { totalRating: 0, avgRating: 0 };
  }

  // Tính tổng rating
  const totalRating = reviews.reduce((acc, item) => {
    // Kiểm tra rating có hợp lệ không
    return acc + (item.rating ? item.rating : 0);
  }, 0);

  // Tính avgRating
  const avgRating = (totalRating / reviews.length).toFixed(1);

  return {
    totalRating,
    avgRating,
  };
};
