import React, { useContext } from "react";
import { Container, Row, Col, Form, FormGroup, Button } from "reactstrap";
import { Link, useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios'; // Import axios
import "../styles/login.css";

import loginImg from "../assets/images/login.png";
import userIcon from "../assets/images/user.png";
import { AuthContext } from "./../context/AuthContext";
import { BASE_URL } from "./../utils/config";

const Login = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const { dispatch } = useContext(AuthContext);
  const navigate = useNavigate();
  const [error, setError] = React.useState(null);

  const onSubmit = async (data) => {
    setError(null);
    dispatch({ type: "LOGIN_START" });

    try {
      // Sử dụng axios để gửi yêu cầu
      const res = await axios.post(`${BASE_URL}/auth/login`, data, {
        withCredentials: true, // Để gửi cookie
      });

      // Dispatch thành công và điều hướng
      dispatch({ type: "LOGIN_SUCCESS", payload: res.data.data });
      navigate("/");
      console.log(res.data);
    } catch (err) {
      setError(err.response?.data?.message || "Login failed."); // Lấy thông báo lỗi từ response
      dispatch({ type: "LOGIN_FAILURE", payload: err.response?.data?.message });
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8" className="m-auto">
            <div className="login_container d-flex justify-content-between">
              <div className="login_img">
                <img src={loginImg} alt="Login" />
              </div>
              <div className="login_form">
                <div className="user">
                  <img src={userIcon} alt="User Icon" />
                </div>
                <h2>Login</h2>
                <Form onSubmit={handleSubmit(onSubmit)}>
                  <FormGroup>
                    <input
                      type="text"
                      placeholder="Email"
                      {...register('email', { required: '* Email is a required' })}
                    />
                    {errors?.email && (<span className="text-danger">{errors.email.message}</span>)}
                  </FormGroup>
                  <FormGroup>
                    <input
                      type="password"
                      placeholder="Password"
                      {...register("password", {
                        required: "* Password is a required field",
                        pattern: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/,
                      })}
                    />
                    {errors.password && (
                      <span className="text-danger">
                        {errors.password.message}
                      </span>
                    )}
                  </FormGroup>
                  <Button className="btn secondary__btn auth_btn" type="submit">
                    Login
                  </Button>
                  {error && <p className="text-danger mt-2">{error}</p>}
                </Form>
                <p>
                  Don't have an account?{" "}
                  <Link to="/register">Create an account</Link>
                </p>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Login;
