import React, { useContext } from "react";
import { Container, Row, Col, Form, FormGroup, Button } from "reactstrap";
import { Link, useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import "../styles/login.css";

import loginImg from "../assets/images/login.png";
import userIcon from "../assets/images/user.png";
import { AuthContext } from "./../context/AuthContext";
import { BASE_URL } from "./../utils/config";

const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const { dispatch } = useContext(AuthContext);
  const navigate = useNavigate();
  const [error, setError] = React.useState(null);

  const onSubmit = async (data) => {
    setError(null);
    dispatch({ type: "LOGIN_START" });

    try {
      const res = await fetch(`${BASE_URL}/auth/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(data), // Sử dụng data từ useForm
      });

      if (!res.ok) {
        const result = await res.json();
        throw new Error(result.message || "Failed to login.");
      }

      const result = await res.json();
      dispatch({ type: "LOGIN_SUCCESS", payload: result.data });
      navigate("/");
      console.log(result);
      
    } catch (err) {
      setError(err.message);
      dispatch({ type: "LOGIN_FAILURE", payload: err.message });
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8" className="m-auto">
            <div className="login_container d-flex justify-content-between">
              <div className="login_img">
                <img src={loginImg} alt="Login" />
              </div>
              <div className="login_form">
                <div className="user">
                  <img src={userIcon} alt="User Icon" />
                </div>
                <h2>Login</h2>
                <Form onSubmit={handleSubmit(onSubmit)}>
                  <FormGroup>
                    <input
                      type="text"
                      placeholder="Email"
                      required
                      {...register("email", {required: "Email is a required field"})}
                    />
                    {errors.email && <p className="text-danger">{errors.email.message}</p>}
                  </FormGroup>
                  <FormGroup>
                    <input
                      type="password"
                      placeholder="Password"
                      required
                      {...register("password")} // Đăng ký trường password với react-hook-form
                    />
                    {errors.password && <p className="text-danger">{errors.password.message}</p>}
                  </FormGroup>
                  <Button className="btn secondary__btn auth_btn" type="submit">
                    Login
                  </Button>
                  {error && <p className="text-danger mt-2">{error}</p>}
                </Form>
                <p>
                  Don't have an account?{" "}
                  <Link to="/register">Create an account</Link>
                </p>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Login;
