import React, { useState, useEffect } from "react";
import "../styles/tour.css";
import TourCard from "./../shared/TourCard";
import Newsletter from "./../shared/Newsletter";
import CommonSection from "../shared/CommonSection";
import { Container, Row, Col } from "reactstrap";
import axios from "axios";
import { BASE_URL } from "../utils/config";

const Tours = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tourCount, setTourCount] = useState(0);

  // Hàm để lấy số lượng tour
  const fetchTourCount = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/tours/search/getTourCount`);
      setTourCount(response.data);
    } catch (err) {
      setError(err);
    }
  };

  // Hàm để lấy danh sách tour
  const fetchTours = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/tours?page=${page}`);
      if (Array.isArray(response.data)) {
        setTours(response.data);
      } else {
        setTours([]); // Nếu không phải là mảng, đặt tours là mảng rỗng
        console.error("Expected an array but received:", response.data);
      }
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTourCount();
  }, []);

  useEffect(() => {
    fetchTours();
  }, [page]);

  useEffect(() => {
    const validTourCount = Math.max(tourCount, 0); // Đảm bảo tourCount không âm
    const pages = Math.ceil(validTourCount / 8);
    setPageCount(pages);
    window.scrollTo(0, 0);
  }, [tourCount]);

  return (
    <>
      <CommonSection title={"All Tours"} />
      <section className="pt-0 mt-xl-5">
        <Container>
          {loading && <h3 className="text-center pt-5">Loading...</h3>}
          {error && <h3 className="text-center pt-5">Error: {error.message}</h3>}
          {!loading && !error && (
            <Row>
              {tours.length > 0 ? (
                tours.map((tour) => (
                  <Col lg="3" md='6' sm='6' className="mb-4" key={tour._id}>
                    <TourCard tour={tour} />
                  </Col>
                ))
              ) : (
                <h3 className="text-center pt-5">No tours found</h3>
              )}
              <Col lg="12">
                <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                  {pageCount > 0 && [...Array(pageCount).keys()].map((number) => (
                    <span
                      key={number}
                      onClick={() => setPage(number)}
                      className={page === number ? "active_page" : ""}
                    >
                      {number + 1}
                    </span>
                  ))}
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default Tours;
